mapper.update:
UPDATE company
SET com_id = #{com_id}, com_pw = #{com_pw}, com_name = #{com_name}, com_size = #{com_size},
    com_ceo = #{com_ceo}, com_addr = #{com_addr}, com_companynum = #{com_companynum},
    com_tel = #{com_tel}, com_email = #{com_email}, com_url = #{com_url}, make_date = #{make_date}
WHERE com_no = #{com_no}

mapper.select:
SELECT c.* FROM company c
INNER JOIN com_interest ci ON c.com_no = ci.com_no
WHERE ci.mem_no = #{mem_no}
